$.Class("Base_MergeRecords_JS",{},{container:false,registerListEvents:function(){this.container.find('[name="record"]').on("change",e=>{this.container.find("input[value="+$(e.currentTarget).val()+"]").trigger("click")});this.container.find('[type="submit"]').on("click",e=>{e.preventDefault;const progressIndicatorElement=$.progressIndicator({position:"html",blockInfo:{enabled:true}});AppConnector.request(this.container.find("form").serializeFormData()).then(function(data){progressIndicatorElement.progressIndicator({mode:"hide"});if(data.result===false){Vtiger_Helper_Js.showPnotify({text:app.vtranslate("JS_ERROR")})}app.hideModalWindow();const listInstance=new Vtiger_List_Js;listInstance.getListViewRecords();Vtiger_List_Js.clearList()})})},registerEvents:function(modalContainer){this.container=$(modalContainer);this.registerListEvents()}});